import React from 'react'
import './Contact.css'
import nodemailer from 'nodemailer'
import {useState} from 'react'

const SecondSection = () => {

    /*
    service - mailing service
    auth - authentication, put the sender's email and password here
    user - sender email
    pass - sender pw
    */
    var transporter = nodemailer.createTransport({ 
       service: 'gmail',
       auth: { 
           user: 'swengdummy4@gmail.com',
           pass: 'mimarimimajomi'
       } 
    });

    /**
     * 
     * from - sender email
     * to - receiver email (company email)
     * subject - mail subject 
     * text - mail body 
     */
    var mailOptions = {
        from: 'swengdummy4@gmail.com',
        to: 'benito_flores@dlsu.edu.ph',
        subject: 'this.state'
    }

    const nameHandler = e => {
        this.setState({name: e.target.value});
    }

    const emailHandler = e => {
        this.setState({email: e.target.value});
    }

    const bodyHandler = e => {
        this.setState({body: e.target.value});
    }

    // just a temporary function, does nothing but display into the broswer console
    const temp = e => {
        console.log(this.name.value + "\n" + this.email.value + "\n" + this.body.value)
    }

    return (
        <div className='second-contact'>

            <div className='second-contact-left'>
                <h1>
                    CONTACT <br/>
                    FORM
                </h1>
            </div>

            <div className='second-contact-right'>
                <form id='contactForm' onSubmit={this.temp}>
                    <input placeholder='Name' name='name' value={this.state.name} onChange={this.nameHandler}/> <br/>
                    <input placeholder='Email' name='email' value={this.state.email} onChange={this.emailHandler}/> <br/>
                    <textarea placeholder='Put your message here...' rows='9' cols='80' name='body' value={this.state.body} onChange={this.bodyHandler} /> <br/>
                    <button type='submit' className='form-button'>SEND</button>
                </form>
            </div>
            
        </div>
    )
}

export default SecondSection
